namespace mihaildev\ckeditor;

use yii\web\AssetBundle;

class Assets extends AssetBundle{
	public $sourcePath = '@mihaildev/ckeditor/editor';

    public $js = [
        'ckeditor.js',
		'js.js',
    ];

	public $depends = [
		'yii\web\YiiAsset',
	];
	public function  init()
	{
		$components = \Yii::$app->getComponents();
		if(!empty($components)){
			if(!(isset($components['assetManager']['bundles']['yii\web\JqueryAsset']) && $components['assetManager']['bundles']['yii\web\JqueryAsset']['sourcePath'] )){
				$components['assetManager']['bundles']['yii\web\JqueryAsset']['sourcePath'] = '@bower/jquery/dist';
				$components['assetManager']['bundles']['yii\web\JqueryAsset']['js'] = [
					'jquery.js',
				];
				\Yii::$app->setComponents($components);
			}
		}
		parent::init(); // TODO: Change the autogenerated stub
	}
}